func TestDefaults(t *testing.T) {
	cfg := new(Config)
	setDefaults(cfg)

	if cfg.Difficulty == nil {
		t.Error("expected difficulty to be non nil")
	}

	if cfg.Time == nil {
		t.Error("expected time to be non nil")
	}
	if cfg.GasLimit == 0 {
		t.Error("didn't expect gaslimit to be zero")
	}
	if cfg.GasPrice == nil {
		t.Error("expected time to be non nil")
	}
	if cfg.Value == nil {
		t.Error("expected time to be non nil")
	}
	if cfg.GetHashFn == nil {
		t.Error("expected time to be non nil")
	}
	if cfg.BlockNumber == nil {
		t.Error("expected block number to be non nil")
	}
}

func TestEVM(t *testing.T) {
	defer func() {
		if r := recover(); r != nil {
			t.Fatalf("crashed with: %v", r)
		}
	}()

	Execute([]byte{
		byte(vm.DIFFICULTY),
		byte(vm.TIMESTAMP),
		byte(vm.GASLIMIT),
		byte(vm.PUSH1),
		byte(vm.ORIGIN),
		byte(vm.BLOCKHASH),
		byte(vm.COINBASE),
	}, nil, nil)
}

func TestExecute(t *testing.T) {
	ret, _, _, err := Execute([]byte{
		byte(vm.PUSH1), 10,
		byte(vm.PUSH1), 0,
		byte(vm.MSTORE),
		byte(vm.PUSH1), 32,
		byte(vm.PUSH1), 0,
		byte(vm.RETURN),
	}, nil, nil)
	if err != nil {
		t.Fatal("didn't expect error", err)
	}

	num := new(big.Int).SetBytes(ret)
	if num.Cmp(big.NewInt(10)) != 0 {
		t.Error("Expected 10, got", num)
	}
}

func TestCall(t *testing.T) {
	db, _ := ethdb.NewMemDatabase()
	state, _ := state.New(common.Hash{}, state.NewDatabase(db))
	address := common.HexToAddress("0x0a")
	state.SetCode(address, []byte{
		byte(vm.PUSH1), 10,
		byte(vm.PUSH1), 0,
		byte(vm.MSTORE),
		byte(vm.PUSH1), 32,
		byte(vm.PUSH1), 0,
		byte(vm.RETURN),
	})

	ret, _, _, err := Call(address, nil, &Config{State: state})
	if err != nil {
		t.Fatal("didn't expect error", err)
	}

	num := new(big.Int).SetBytes(ret)
	if num.Cmp(big.NewInt(10)) != 0 {
		t.Error("Expected 10, got", num)
	}
}

func TestTransactionReverted(t *testing.T) {
	/*
	 	   	Contract Source Code
	 	   	```
	 	   	contract Demo {
	 	       		function Demo() {}
	 	       		function IllegalDivision() returns(int) {
	 	           		var dividend = 0;
	 	           		return 1 / dividend;
	 	       		}
	 	       		function LegalDivision() returns(int) {
	 	           		var dividend = 1;
	 	           		return 1 / dividend;
	        		}
	 	   	}
	 	   	```
	*/
	var definition = `[{"constant":false,"inputs":[],"name":"LegalDivision","outputs":[{"name":"","type":"int256"}],"payable":false,"type":"function"},{"constant":false,"inputs":[],"name":"IllegalDivision","outputs":[{"name":"","type":"int256"}],"payable":false,"type":"function"},{"inputs":[],"payable":false,"type":"constructor"}]`
	var rawcode = common.Hex2Bytes("")

	abi, err := abi.JSON(strings.NewReader(definition))
	if err != nil {
		t.Fatal(err)
	}

	legalDivision, err := abi.Pack("LegalDivision")
	if err != nil {
		t.Fatal(err)
	}

	illegalDivision, err := abi.Pack("IllegalDivision")
	if err != nil {
		t.Fatal(err)
	}
	var failed bool
	// deploy
	cfg := &Config{
		Origin: common.HexToAddress("sender"),
	}
	code, _, _, _, err := Create(rawcode, cfg)
	if err != nil {
		t.Fatal(err)
	}
	_, _, failed, _ = Execute(code, legalDivision, cfg)
	if failed != false {
		t.Fatal("Expect false, got true")
	}
	_, _, failed, _ = Execute(code, illegalDivision, cfg)
	if failed != true {
		t.Fatal("Expect true, got false")
	}
}

func TestDelegateReverted(t *testing.T) {
	/*
	 		Contract Source Code
	 		```
	 		contract Relay {
	 		    address public currentVersion;
	 		    address public owner;

			    modifier onlyOwner() {
	 			if (msg.sender != owner) {
	 			    throw;
	 			}
	 			_;
	 		    }
	 		    function Relay(address _address) {
	 			currentVersion = _address;
	 			owner = msg.sender;
	 		    }
	 		    function changeContract(address newVersion) public
	 		    onlyOwner()
	 		    {
	 			currentVersion = newVersion;
	 		    }
	 		    function() {
	 			if(!currentVersion.delegatecall(msg.data)) throw;
	 		    }
			}

	 		contract Demo {
	 		    function Demo() {
	 		    }
	 		    function IllegalDivision() returns(int) {
	 			var dividend = 0;
	 			return 1 / dividend;
	 		    }
	 		    function LegalDivision() returns(int) {
	 			var dividend = 1;
	 			return 1 / dividend;
	 		    }
	 		}
	 		```
	*/
	var definition = `[{"constant":false,"inputs":[],"name":"LegalDivision","outputs":[{"name":"","type":"int256"}],"payable":false,"type":"function"},{"constant":false,"inputs":[],"name":"IllegalDivision","outputs":[{"name":"","type":"int256"}],"payable":false,"type":"function"},{"inputs":[],"payable":false,"type":"constructor"}]`
	var rawcode1 = common.Hex2Bytes("")
	var rawcode2 = common.Hex2Bytes("")

	abi, err := abi.JSON(strings.NewReader(definition))
	if err != nil {
		t.Fatal(err)
	}

	legalDivision, err := abi.Pack("LegalDivision")
	if err != nil {
		t.Fatal(err)
	}

	illegalDivision, err := abi.Pack("IllegalDivision")
	if err != nil {
		t.Fatal(err)
	}
	var failed bool
	// deploy
	cfg := &Config{
		Origin: common.HexToAddress("sender"),
	}
	_, addr, _, _, err := Create(rawcode1, cfg)
	if err != nil {
		t.Fatal(err)
	}

	_, addr2, _, _, err := Create(append(rawcode2, common.LeftPadBytes(addr.Bytes(), 32)...), cfg)
	if err != nil {
		t.Fatal(err)
	}
	_, _, failed, _ = Call(addr2, legalDivision, cfg)
	if failed != false {
		t.Fatal("Expect false, got true")
	}
	_, _, failed, _ = Call(addr2, illegalDivision, cfg)
	if failed != true {
		t.Fatal("Expect true, got false")
	}
}

func BenchmarkCall(b *testing.B) {
	var definition = `[{"constant":true,"inputs":[],"name":"seller","outputs":[{"name":"","type":"address"}],"type":"function"},{"constant":false,"inputs":[],"name":"abort","outputs":[],"type":"function"},{"constant":true,"inputs":[],"name":"value","outputs":[{"name":"","type":"uint256"}],"type":"function"},{"constant":false,"inputs":[],"name":"refund","outputs":[],"type":"function"},{"constant":true,"inputs":[],"name":"buyer","outputs":[{"name":"","type":"address"}],"type":"function"},{"constant":false,"inputs":[],"name":"confirmReceived","outputs":[],"type":"function"},{"constant":true,"inputs":[],"name":"state","outputs":[{"name":"","type":"uint8"}],"type":"function"},{"constant":false,"inputs":[],"name":"confirmPurchase","outputs":[],"type":"function"},{"inputs":[],"type":"constructor"},{"anonymous":false,"inputs":[],"name":"Aborted","type":"event"},{"anonymous":false,"inputs":[],"name":"PurchaseConfirmed","type":"event"},{"anonymous":false,"inputs":[],"name":"ItemReceived","type":"event"},{"anonymous":false,"inputs":[],"name":"Refunded","type":"event"}]`

	var code = common.Hex2Bytes("")

	abi, err := abi.JSON(strings.NewReader(definition))
	if err != nil {
		b.Fatal(err)
	}

	cpurchase, err := abi.Pack("confirmPurchase")
	if err != nil {
		b.Fatal(err)
	}
	creceived, err := abi.Pack("confirmReceived")
	if err != nil {
		b.Fatal(err)
	}
	refund, err := abi.Pack("refund")
	if err != nil {
		b.Fatal(err)
	}

	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		for j := 0; j < 400; j++ {
			Execute(code, cpurchase, nil)
			Execute(code, creceived, nil)
			Execute(code, refund, nil)
		}
	}
}
